import { Component, OnInit } from '@angular/core';

import { HttpClient,HttpResponse } from '@angular/common/http';
import Swal from "sweetalert2";


@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit {
  private url = 'http://localhost:8080/currency?date=01032021';
  gets:any;
  userInput ?: string;
  constructor(private service:PostService,private httpClient:HttpClient) {}

  ngOnInit() {
   this.getPostsFromComponent();
  }


  getPostsFromComponent(){
    this.url='http://localhost:8080/currency?date='+this.userInput;
    console.log(this.url)
    return this.httpClient.get(this.url) .subscribe(response => {
      this.gets = response;
    });
  }

  tinyAlert() {
    Swal.fire('HELLOOOOOOOOO!');
  }


}
