import { Injectable } from '@angular/core';
import {HttpClient, HttpHeaders} from '@angular/common/http';
import Swal from 'sweetalert2';
import {catchError, map, throwError} from "rxjs";
@Injectable({
  providedIn: 'root'
})
export class PostService {
  private url = 'http://localhost:8080/currency?date=01032021';
  private headers?: HttpHeaders | { [p: string]: string | string[] };

  constructor(private httpClient: HttpClient) { }

  getPosts(userInput: string | undefined){
    this.url='http://localhost:8080/currency?date='+userInput;
    console.log(this.url)
    return this.httpClient.get(this.url,{ headers: this.headers, observe: 'response' })
      .pipe(
        map((response: { status: string | number; }) => {
          if (response.status === 200) {
            return response;
          } else {
            throw new Error('Request failed with status code ' + response.status);
          }
        }),
        catchError(error => {
          return throwError(error);
        })
      );
  }

  tinyAlert() {
    Swal.fire('Hey there!');
  }





}
